// Styled Components for Animation
const SVGContainer = styled.div`
  min-width: 150px;
  min-height: 150px;
  transform: rotate(90deg);

  /* Adjust animation speed dynamically */
  .fan {
      width: 150px;
    animation: rotateFan ${({ speed }) => speed}s linear infinite;
  }

  @keyframes rotateFan {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }
`;
const UsageText = styled.div`
  position: absolute;
  color: #ff007f;
  font-size: 18px;
  font-weight: bold;
  text-align: center;
  display: flex;
  width: 250px;
  justify-content: center;
      justify-content: center;
    align-content: flex-start;
    align-items: flex-start;
`;

export default FanSVG;
